function fetchData(){fetch("https://us-east-1.aws.data.mongodb-api.com/app/azoio-evvkb/endpoint/quizzes",{method:"GET",headers:{"Content-Type":"application/json"}}).then((e=>e.json())).then((e=>generateHTML(e))).catch((e=>{console.log("Error:",e)}))}function generateHTML(e){const t=document.getElementById("quizzes");e=[e],console.log(e),e.forEach((e=>{const n=`\n        <div class="bg-white rounded-md ">\n            <div class="px-6 py-24 sm:px-6 sm:py-32 lg:px-8">\n                <div class="mx-auto max-w-lg text-center">\n                    <p class="text-base font-semibold leading-7 text-pink-600">Quiz Time</p>\n                    <h2 class="text-3xl font-bold tracking-tight text-gray-900 sm:text-4xl">${e.title}</h2>\n                    <div class="mt-10 flex items-center justify-center gap-x-6">\n                        <a href="https://quiz.allwomenstalk.com/${e._id}" class="rounded-md bg-pink-600 px-3.5 py-2.5 text-sm font-semibold text-white shadow-sm hover:bg-pink-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600">Play now</a>\n                    </div>\n                </div>\n            </div>\n        </div>`;t.innerHTML+=n}))}const observer=new IntersectionObserver(((e,t)=>{e.forEach((e=>{e.isIntersecting&&(console.log("Intersecting"),fetchData(),t.disconnect())}))}),{rootMargin:"0px 0px -10px 0px"});observer.observe(document.getElementById("quizzes"));